CREATE TABLE IF NOT EXISTS `grade` (
`ID` int(11) unsigned NOT NULL auto_increment,
`label` varchar(255) NOT NULL,
`gradeValue` int(2) unsigned NOT NULL,
Primary Key (`ID`)
);

CREATE TABLE IF NOT EXISTS `schoolYear` (
`ID` int(11) unsigned NOT NULL auto_increment,
`label` varchar(255) NOT NULL,
`active` BOOLEAN NOT NULL,
Primary Key (`ID`)
);

		CREATE TABLE IF NOT EXISTS `admin_groups` (
                        `ID` smallint(11) unsigned NOT NULL auto_increment,
                        `name` varchar(255) NOT NULL,
                        `modules` varchar(1024) NOT NULL,
                        PRIMARY KEY  (`ID`)
                    )AUTO_INCREMENT=1;

CREATE TABLE IF NOT EXISTS `jointUsersInSchoolYear` (
`ID` int(11) unsigned NOT NULL auto_increment,
`UserID` int(11) unsigned NOT NULL,
`SchoolYearID` int(11) unsigned NOT NULL,
Primary Key (`ID`)
);

CREATE TABLE IF NOT EXISTS `jointUsersInGrade` (
`ID` int(11) unsigned NOT NULL auto_increment,
`UserID` int(11) unsigned NOT NULL,
`GradeID` int(11) unsigned NOT NULL,
Primary Key (`ID`)
);

CREATE TABLE IF NOT EXISTS `usersInClassStatus` (
`ID` int(11) unsigned NOT NULL auto_increment,
`name` varchar (64) NOT NULL,
`translatedName` varchar(255) NOT NULL,
Primary Key (`ID`)
);

CREATE TABLE IF NOT EXISTS `jointClassInSchoolYear` (
`ID` int(11) unsigned NOT NULL auto_increment,
`ClassID` int(11) unsigned NOT NULL,
`SchoolYearID` int(11) unsigned NOT NULL,
Primary Key (`ID`)
);

CREATE TABLE IF NOT EXISTS `jointClassTeacherInClass` (
`ID` int(11) unsigned NOT NULL auto_increment,
`ClassTeacherID` int(11) unsigned NOT NULL,
`ClassID` int(11) unsigned NOT NULL,
Primary Key (`ID`)
);

CREATE TABLE IF NOT EXISTS `jointGradeInSchoolYear` (
`ID` int(11) unsigned NOT NULL auto_increment,
`GradeID` int(11) unsigned NOT NULL,
`SchoolYearID` int(11) unsigned NOT NULL,
Primary Key (`ID`)
);

		    CREATE TABLE IF NOT EXISTS `global_settings` (
                                `id` smallint(5) unsigned NOT NULL auto_increment,
                                `name` varchar(255) NOT NULL,
